        //nums can only have [1 to 9] and combinantions will not contain duplication
        //prepare dummy muns[]
        int[] nums = new int[9];
        for (int i = 0; i < 9; i++) {
            nums[i] = i + 1;
        }
        combinationSum_3_Helper(nums, targetSum, kNums, 0, new ArrayList<>(), result);
