    public boolean checkIfOneCharSwapMakeStringEqual(String s1, String s2) {

        //https://leetcode.com/problems/check-if-one-string-swap-can-make-strings-equal/
        if (s1.length() != s2.length()) {
            return false;
        }

        int n = s1.length();

        char swapedA = '.';
        char swapedB = '.';
        char swapedC = '.';
        char swapedD = '.';

        int swapCount = 0;

        for (int i = 0; i < n; i++) {

            if (s1.charAt(i) != s2.charAt(i)) {
                swapCount++;
                if (swapCount == 1) {
                    swapedA = s1.charAt(i);
                    swapedB = s2.charAt(i);
                } else if (swapCount == 2) {
                    swapedC = s1.charAt(i);
                    swapedD = s2.charAt(i);
                } else {
                    return false;
                }
            }
        }

        return swapCount == 0
                || (swapedA == swapedD && swapedB == swapedC);
    }

