        int n = nums.length;
        Arrays.sort(nums);
        int partition = 1;
        int prevIndex = 0;

        for (int i = 1; i < n; i++) {
            int currDiffInMaxAndMinVals = nums[i] - nums[prevIndex];
            if (currDiffInMaxAndMinVals > k) {
                partition++;
                prevIndex = i;
            }
        }
